package libroJava;

public class Metodo_toLowerCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	String cadena = "NOS CONVERTIMOS EN LO QUE QUEREMOS SER";
		
		System.out.println("Cadena normal: " + cadena);
		System.out.println("Cadena modificada: " + cadena.toLowerCase());

	}

}
