package libroJava;

public class Metodo_toUpperCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String cadena = "Nos convertimos en lo que queremos ser";
		
		System.out.println("Cadena normal: " + cadena);
		System.out.println("Cadena modificada: " + cadena.toUpperCase());
	}

}
